import { useState } from "react";
const RegisterPage = () => {
    const [role,setRole]=useState('patient');
    const renderInputForm = () => {
        if (selectedOption === 'email') {
          return <input type="email" placeholder="Enter your email" />;
        } else if (selectedOption === 'username') {
          return <input type="text" placeholder="Enter your username" />;
        } else if (selectedOption === 'password') {
          return <input type="password" placeholder="Enter your password" />;
        } else {
          return null;
        }
      };
    return ( 
         <div className="register-page">
            <div className="content">
               <label>Select User Type</label>
               <select 
               value={role}
               onChange={(e)=>setRole(e.target.value)}>
                <option value="patient">Patient</option>
                <option value="doctor">Doctor</option>
                <option value="insuranceProvider">Insurance Provider </option>
               </select>
               {role && renderInputForm()}
            </div>
         </div>
     );
}

export default RegisterPage;
